sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.imports
 */
function evaluateProperty({
    api,
    helpers
}) {

    var KPI_bds_list = api.data.verticalA_headlinemetric1v.output.result;
    
    const selection_arr = [api.state.tab3PISElement, api.state.tab3PortfolioElement];
    var dropdown_selection = selection_arr.find(el => el !== '');

    if (api.state.tab3PISBreakdown != '') {
        for(let i = 0; i < KPI_bds_list.length; i++) {
          var KPI_bd_sysid = KPI_bds_list[i].element_level2.value;
          if (dropdown_selection === KPI_bd_sysid) {
          var KPI_bd_score = KPI_bds_list[i].scores[0].value;
          break;
          }
        }
      } else {
          var KPI_bd_score = KPI_bds_list[0].scores[0].value;
        }

    var val = KPI_bd_score;

    if (val < 13) {
        return '* { font-size: 24px; font-weight: 600; color:#008000}';
    } else if (val >= 13 && val < 20) {
        return '* { font-size: 24px; font-weight: 600; color:#FFA500}';
    } else {
        return '* { font-size: 24px; font-weight: 600; color:#FF0000}';
    }
}
