sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    var arr_lm = [];
    var arr_cm = api.state.verticalCMetric2ShowHealth;
    var results = api.data.verticalC_headlinemetric2.output.result;
    var clientStateParameter = 'verticalCMetric2ShowChange';

    for (let i = 0; i < results.length; i++) {
        var value_lm = results[i].scores[1].value;
        if (value_lm<1 && value_lm>0) {
            arr_lm.push('green');
        } else if (value_lm>=1 && value_lm<=7) {
            arr_lm.push('amber');
        } else if (value_lm>7){
            arr_lm.push('red');
        } else {
            arr_lm.push('');
        }
    }

    const arr_change = [];

    for (let i = 0; i < arr_cm.length; i++) {
        const current = arr_cm[i];
        const last = arr_lm[i];
        if (current === '' || last === '') {
            arr_change.push('');
        } else if (current === last) {
            arr_change.push('same');
        } else if ((current === 'green' && last === 'amber') || 
                   (current === 'amber' && last === 'red')) {
            arr_change.push('better');
        } else {
            arr_change.push('worse');
        }
    }

    api.setState(clientStateParameter, arr_change);
}
