sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    var KPI_bds_list = api.data.verticalD_headlinemetric2v.output.result;
    var dropdown_selection = api.state.tab3PortfolioElement;
        
    for(let i = 0; i < KPI_bds_list.length; i++) {
        var KPI_bd_sysid = KPI_bds_list[i].element.value;
        if (dropdown_selection === KPI_bd_sysid) {
            var KPI_bd_score = KPI_bds_list[i].scores[0].value;
          break;
        }
    }

    var value  = KPI_bd_score;
    if(value>20){
        api.setState('tab3verticalDMetric2ShowHealth','green');
    }
    else if (value>=10 && value<=20 ){
        api.setState('tab3verticalDMetric2ShowHealth','amber');
    }
    else if (value<10 && value>0 ) {
        api.setState('tab3verticalDMetric2ShowHealth','red');
    }
    else {
        api.setState('tab3verticalDMetric2ShowHealth','');
    }

}
