sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    var KPI_bds_list = api.data.verticalC_headlinemetric1v.output.result;
    var dropdown_selection = api.state.tab3PortfolioElement;
       
    if (api.state.tab3BreakdownSource != '') {
        for(let i = 0; i < KPI_bds_list.length; i++) {
            var KPI_bd_sysid = KPI_bds_list[i].element.value;
            if (dropdown_selection === KPI_bd_sysid) {
                var KPI_bd_score = KPI_bds_list[i].scores[0].value;
              break;
            }
        }
    } else {
        var KPI_bd_score = KPI_bds_list[0].scores[0].value;
    }

    var value  = KPI_bd_score;
    if(value<5.5 && value>0){
        api.setState('tab3verticalCMetric1ShowHealth','green');
    }
    else if (value>=5.5 && value<=6.5 ){
        api.setState('tab3verticalCMetric1ShowHealth','amber');
    }
    else if (value>6.5 ) {
        api.setState('tab3verticalCMetric1ShowHealth','red');
    }
    else {
        api.setState('tab3verticalCMetric1ShowHealth','');
    }

}
