sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    var Portfolio_list = [];
    var PIS_list = [];
    var dropdown_selection = api.state.tab3PortfolioElement;
    var clientStateParameter = 'tab3BreakdownSource';

    for (var i = 0; i < api.data.dropdown_portfolio.results.length; i++) {
        var portfolio_element = api.data.dropdown_portfolio.results[i].sys_id.value;
        Portfolio_list.push(portfolio_element);
    }
    for (var i = 0; i < api.data.dropdown_pis.results.length; i++) {
        var pis_element = api.data.dropdown_pis.results[i].sys_id.value;
        PIS_list.push(pis_element);
    }

    if (Portfolio_list.includes(dropdown_selection)) {
        var portfolio_breakdown_sysid = 'e6def6531bf5fc50550b97d0b24bcb21';
        api.setState(clientStateParameter, portfolio_breakdown_sysid);
    }
    else if (PIS_list.includes(dropdown_selection)) {
        var pis_breakdown_sysid = 'b1616b8c4fe933002f3c71318110c743';
        api.setState(clientStateParameter, pis_breakdown_sysid);
    }
}
