sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    var indicator = api.data.getmetricsofciodashboard.output.VerticalA.supportingMetrics[1].metricSysid;

    var PortfolioBreakdown = api.state.tab3PortfolioBreakdown;
    var PortfolioElement = api.state.tab3PortfolioElement;
    var PISBreakdown = api.state.tab3PISBreakdown;
    var PISElement = api.state.tab3PISElement;

    var breakdown = [];
    if (PortfolioBreakdown != '' && PISBreakdown != '') {
        breakdown = [{
                "sysId": PortfolioBreakdown,
                "element_sys_ids": [
                    PortfolioElement
                ]
            },
            {
                "sysId": PISBreakdown,
                "element_sys_ids": [
                    PISElement
                ]
            }
        ];
    } else if (PortfolioBreakdown != '') {
        breakdown = [{
            "sysId": PortfolioBreakdown,
            "element_sys_ids": [
                PortfolioElement
            ]
        }];
    } else {
        breakdown = [];
    }

    var datasources = [{
        "metrics": [{
            "dataSource": "tab3datasourceID_VASM2",
            "id": "tab3metricID_VASM2",
            "aggregateIndicator": "",
            "frequency": 10,
            "numberFormat": {
                "customFormat": false
            },
            "axisId": "primary"
        }],
        "datasources": [{
            "sourceType": "indicator",
            "uuid": {
                "indicator": indicator,
                "breakdowns": breakdown
            },
            "id": "tab3datasourceID_VASM2"
        }],
        "trendBy": {
            "trendByFrequency": "date",
            "trendByFields": [{
                "field": "anything",
                "metric": "tab3metricID_VASM2"
            }]
        },
        "groupBy": [{
            "maxNumberOfGroups": 10,
            "numberOfGroupsBasedOn": "NO_OF_GROUP_BASED_ON_PER_METRIC",
            "showOthers": false,
            "hideZeroValues": false,
            "groupBy": [{
                "dataSource": "tab3datasourceID_VASM2",
                "groupByField": breakdown,
                "isRange": false,
                "isPaBucket": false
            }]
        }]
    }];

    // var datasources = [...api.state.datasources];
    // datasources[0].uuid = {...datasources[0].uuid, indicator:indicator};
    api.setState("tab3verticalASupportingMetric2", datasources);
}
