sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
// */

function evaluateProperty({
    api,
    event,
    helpers,
    imports
}) {
    var m1health = api.state.verticalCMetric1ShowHealth;
    var m2health = api.state.verticalCMetric2ShowHealth;
    var clientStateParameter = 'verticalCShowHealth';

    if ((m1health === 'green' && (m2health === 'green' || m2health === 'amber' || m2health === '')) ||
        (m2health === 'green' && (m1health === 'green' || m1health === 'amber' || m1health === ''))) {
        api.setState(clientStateParameter, 'green');
    } else if ((m1health === 'red' && (m2health === 'red' || m2health === '')) ||
        (m2health === 'red' && (m1health === 'red' || m1health === ''))) {
        api.setState(clientStateParameter, 'red');
    } else if (m1health === '' && m2health === '') {
        api.setState(clientStateParameter, '');
    } else {
        api.setState(clientStateParameter, 'amber');
    }
}
